const buyButtons=document.querySelectorAll(".button-buy"),basketModal=document.querySelector(".modal-basket"),closeBasket=basketModal.querySelector(".modal-close");console.log(buyButtons.length);for(let e=0;e<buyButtons.length;e++)buyButtons[e].addEventListener("click",function(e){e.preventDefault(),basketModal.classList.add("modal-show")});closeBasket.addEventListener("click",function(e){e.preventDefault(),basketModal.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&basketModal.classList.contains("modal-show")&&(e.preventDefault(),basketModal.classList.remove("modal-show"))});
